import barclays from '../Component/images/barclays.png';
import '../Component/css/App.css';
import { Link, useLocation } from "react-router-dom";
const Industry_Profile = ({ industryData }) => {
    const location = useLocation();
    const state = location.state || { profile: { name: "Digital Futures", description: "Its a training academy", location: "London" } };
    return (
        <>
            <body class='body'>

                < div className="container shadow p-3 mb-5 bg-body rounded" >
                    <div className="row">
                        <div className="col">
                            <div className="row">
                                <div className="col">
                                    <h3>{state?.profile?.name}</h3>
                                </div>
                                <div className="col edit">
                                    <Link to="/edit">Edit Profile</Link>
                                </div>
                            </div>
                            <p>{state?.profile?.description}</p>
                            <p>{state?.profile?.location}</p>
                        </div>
                        <div className="col col2">
                            <img src={`${state?.profile?.image}`} alt="Industry Logo" className="img-fluid" />
                        </div>
                    </div>
                </div>
            </body>
        </>
    )
}

export default Industry_Profile;
